{
  penPlus.categories = penPlus.categories || {};

  class controls_category extends penPlus.penPlusExtension {
    getInfo() {
      return {
        name: "Controls",
        id: "controls",
        color1: "#ffab19",
        color2: "#ec9c13",
        color3: "#cf8b17",
        blocks: [
          {
            opcode: "if",
            type: "command",
            text: "if %1 then %2 %3",
            tooltip: "If the input is true execute the inline code.",
            arguments: [
              {
                type: "input_value",
                check: "Boolean",
                name: "condition",
              },
              {
                type: "input_dummy",
              },
              {
                type: "input_statement",
                name: "true",
              },
            ],
          },
          {
            opcode: "ifelse",
            type: "command",
            text: "if %1 then %2 %3 else %4 %5",
            tooltip: "If the input is true execute the inline code.",
            arguments: [
              {
                type: "input_value",
                name: "condition",
                check: "Boolean",
              },
              {
                type: "input_dummy",
              },
              {
                type: "input_statement",
                name: "true",
              },
              {
                type: "input_dummy",
              },
              {
                type: "input_statement",
                name: "false",
              },
            ],
          },
          {
            opcode: "repeat",
            type: "command",
            text: ["repeat %1 %2", "%1"],
            alignment: ["LEFT", "RIGHT"],
            tooltip: "Repeats the specified amount of times.",
            arguments: [
              [
                {
                  type: "input_value",
                  name: "times",
                  shadow: {
                    type: "int_reporter",
                  },
                },
                {
                  type: "input_statement",
                  name: "code",
                },
              ],
              [
                {
                  type: "field_image",
                  name: "times",
                  src: "data:image/png;base64,",
                  width: 16,
                  height: 16,
                },
              ],
            ],
          },
          {
            opcode: "upperIndex",
            type: "reporter",
            text: "loop index",
            tooltip: "The loop above this block's index",
          },
          "---",
          {
            opcode: "continue",
            type: "terminal",
            text: "continue %1",
            tooltip: "skips to the next iteration of a loop",
            arguments: [
              {
                type: "field_image",
                name: "times",
                src: "data:image/png;base64,",
                width: 16,
                height: 16,
              },
            ],
          },
          {
            opcode: "break",
            type: "terminal",
            text: "break %1",
            tooltip: "Breaks a loop",
            arguments: [
              {
                type: "field_image",
                name: "times",
                src: "data:image/png;base64,",
                width: 16,
                height: 16,
              },
            ],
          },
          {
            opcode: "discard",
            type: "terminal",
            text: "discard %1",
            tooltip: "discards the current pixel",
            arguments: [
              {
                type: "field_image",
                name: "times",
                src: "data:image/png;base64,",
                width: 16,
                height: 16,
              },
            ],
          },
        ],
      };
    }

    if(block, generator) {
      const condition = generator.valueToCode(block, "condition", Order.ATOMIC);
      const trueExec = generator.statementToCode(block, "true");
      return `if (${condition}) {\n${trueExec}\n}` + nextBlockToCode(block, generator);
    }

    ifelse(block, generator) {
      const condition = generator.valueToCode(block, "condition", Order.ATOMIC);
      const trueExec = generator.statementToCode(block, "true");
      const falseExec = generator.statementToCode(block, "false");
      return `if (${condition}) {\n${trueExec}\n}\nelse{\n${falseExec}\n}` + nextBlockToCode(block, generator);
    }

    repeat(block, generator) {
      const times = generator.valueToCode(block, "times", Order.ATOMIC);
      penPlus.loopID = penPlus.loopID || 0;
      penPlus.loopID += 1;
      const code = generator.statementToCode(block, "code");
      const text = `for (int penPlusLoop_${penPlus.loopID}=0;penPlusLoop_${penPlus.loopID}<int(${times});penPlusLoop_${penPlus.loopID}++) {\n${code}\n}` + nextBlockToCode(block, generator);
      penPlus.loopID -= 1;
      return text;
    }

    upperIndex(block, generator) {
      return [`penPlusLoop_${penPlus.loopID}`, Order.ATOMIC];
    }

    continue() {
      return `continue;\n`;
    }

    break() {
      return `break;\n`;
    }

    discard() {
      return `discard;\n`;
    }
  }

  penPlus.categories.controls = controls_category;
}
